const cartPopup=document.querySelector(".cart-popup"),buttonsBuy=document.querySelectorAll(".product-cards__button--buy"),buttonsFavorites=document.querySelectorAll(".product-cards__button--favorites"),cartPopupClose=cartPopup.querySelector(".cart-popup__close"),menuItemCart=document.querySelector(".user-menu-item--cart"),menuItemFavorites=document.querySelector(".user-menu-item--favorites");for(let e of buttonsBuy)e.addEventListener("click",function(e){e.preventDefault(),cartPopup.classList.add("cart-popup--show"),menuItemCart.classList.add("user-menu-item--added")});for(let e of buttonsFavorites)e.addEventListener("click",function(e){e.preventDefault(),menuItemFavorites.classList.add("user-menu-item--added")});cartPopupClose.addEventListener("click",function(e){e.preventDefault(),cartPopup.classList.remove("cart-popup--show")}),window.addEventListener("keydown",function(e){27===e.keyCode&&cartPopup.classList.contains("cart-popup--show")&&(e.preventDefault(),cartPopup.classList.remove("cart-popup--show"))});